const emailElement=document.querySelector(".contatoemail"),telElement=document.querySelector(".contatotel"),copyEmailBtn=document.querySelector("#copyemail"),copyTelBtn=document.querySelector("#copytel");function copyTextToClipboard(e,t){navigator.clipboard.writeText(e.trim()).then(()=>{showTemporaryMessage(t,"Copiado!")}).catch(()=>{showTemporaryMessage(t,"Falha ao Copiar!")})}function showTemporaryMessage(e,t){const o=e.innerHTML;e.innerHTML=`<span>${t}</span>`,setTimeout(()=>{e.innerHTML=o},1500)}copyEmailBtn.addEventListener("click",()=>{copyTextToClipboard(emailElement.textContent.trim(),copyEmailBtn)}),copyTelBtn.addEventListener("click",()=>{copyTextToClipboard(telElement.textContent.trim().replace(/\s+/g," "),copyTelBtn)});